// Create default form data for weekly trigger
const focusAreas = ['Defence', 'Healthcare', 'Financial', 'Manufacturing'];
const currentWeek = Math.floor(Date.now() / (7 * 24 * 60 * 60 * 1000));
const focusArea = focusAreas[currentWeek % focusAreas.length];

const defaultData = {
  focusArea: focusArea,
  specificTopic: 'Latest industry developments and trends',
  keyMessage: 'Strategic insights for modern RMIS implementation',
  targetAudience: 'Risk Managers, CROs, Industry Leaders',
  callToAction: 'webinar',
  contentType: 'both'
};

return [{json: defaultData}];

const items = $input.all();
console.log("=== NEWS FILTER DEBUG ===");
console.log("Items received:", items.length);

// Get the merged data
const mergedData = items[0]?.json;
console.log("Merged data:", mergedData);

// Since we have mock/merged data instead of NewsAPI articles array,
// we'll create articles based on the focus area
const focusArea = mergedData?.focusArea || 'General';
const specificTopic = mergedData?.specificTopic || 'RMIS trends';

console.log("Focus area:", focusArea);
console.log("Specific topic:", specificTopic);

// Create relevant articles based on focus area
let articles = [];

if (focusArea === 'Defence' || focusArea === 'Defense') {
  articles = [
    {
      title: "Defense Budget Constraints Impact RMIS Implementation",
      description: "Recent DoD spending cuts are forcing defense contractors to reconsider their risk management technology investments and prioritize cost-effective solutions.",
      publishedAt: "2024-07-24T10:00:00Z",
      url: "https://example.com/defense-rmis-budget"
    },
    {
      title: "Military Contractors Adopt Advanced RMIS for Compliance",
      description: "Defense organizations are implementing comprehensive risk management systems to meet stringent DoD compliance requirements while managing operational risks.",
      publishedAt: "2024-07-23T15:30:00Z",
      url: "https://example.com/military-rmis-compliance"
    }
  ];
} else if (focusArea === 'Healthcare') {
  articles = [
    {
      title: "Healthcare RMIS Market Expands Amid Regulatory Changes",
      description: "Healthcare organizations are investing in risk management information systems to handle patient safety, data privacy, and regulatory compliance challenges.",
      publishedAt: "2024-07-24T09:00:00Z",
      url: "https://example.com/healthcare-rmis"
    },
    {
      title: "Hospital Risk Management Systems Show ROI Growth",
      description: "Medical institutions report significant returns on RMIS investments through reduced insurance costs and improved patient safety outcomes.",
      publishedAt: "2024-07-22T14:00:00Z",
      url: "https://example.com/hospital-rmis-roi"
    }
  ];
} else if (focusArea === 'Financial') {
  articles = [
    {
      title: "Financial Services RMIS Adoption Accelerates",
      description: "Banks and financial institutions are rapidly implementing comprehensive risk management systems to handle regulatory compliance and operational risk.",
      publishedAt: "2024-07-24T11:00:00Z",
      url: "https://example.com/financial-rmis"
    },
    {
      title: "RegTech Integration Transforms Banking Risk Management",
      description: "Financial institutions leverage AI-powered RMIS solutions to automate compliance monitoring and risk assessment processes.",
      publishedAt: "2024-07-21T16:00:00Z",
      url: "https://example.com/regtech-banking"
    }
  ];
} else {
  // General/default articles
  articles = [
    {
      title: "RMIS Market Shows Strong Growth Trajectory",
      description: "The global risk management information systems market is projected to reach $1.05 billion by 2030, driven by increasing regulatory requirements and digital transformation.",
      publishedAt: "2024-07-24T12:00:00Z",
      url: "https://example.com/rmis-market-growth"
    },
    {
      title: "AI Integration Revolutionizes Risk Management Systems",
      description: "Organizations are incorporating artificial intelligence and machine learning capabilities into their RMIS platforms to enhance predictive analytics and automated decision-making.",
      publishedAt: "2024-07-23T10:00:00Z",
      url: "https://example.com/ai-rmis-revolution"
    }
  ];
}

console.log("Generated articles count:", articles.length);

// Add the focus area and topic to each article for context
const enrichedArticles = articles.map(article => ({
  json: {
    ...article,
    focusArea: focusArea,
    specificTopic: specificTopic,
    keyMessage: mergedData?.keyMessage || 'Strategic insights',
    // PRESERVE THE COMPLETE FORM DATA
    formData: {
      focusArea: mergedData?.focusArea,
      specificTopic: mergedData?.specificTopic,
      keyMessage: mergedData?.keyMessage,
      targetAudience: mergedData?.targetAudience,
      callToAction: mergedData?.callToAction,
      keyEvents: mergedData?.keyEvents,
      additionalNotes: mergedData?.additionalNotes
    }
  }
}));

console.log("Returning enriched articles");
return [enrichedArticles[0]]; // Return only the first article

