const items = $input.all();

let debugInfo = {
  totalItems: items.length,
  itemStructures: [],
  searchResults: {},
  finalData: {}
};

// Log items to see structure
for (let i = 0; i < items.length; i++) {
  debugInfo.itemStructures.push({
    itemIndex: i,
    itemType: items[i]?.json?.articles ? "NewsAPI Response" : "Form Data",
    hasArticles: !!items[i]?.json?.articles,
    hasBody: !!items[i]?.json?.body,
    fullStructure: items[i]
  });
}

let formData = {};
let newsData = {};

// Look for form data in the FIRST item (before NewsAPI overwrites it)
// The form data should be in items[0] if it exists
const firstItem = items[0];

// Check if this is NewsAPI data or form data
if (firstItem?.json?.articles) {
  // This is NewsAPI response - form data was lost
  debugInfo.dataSource = "NewsAPI only - form data lost";
  newsData = firstItem.json;
} else if (firstItem?.json?.body) {
  // This has form data
  formData = firstItem.json.body;
  debugInfo.dataSource = "Form data found in body";
} else if (firstItem?.json?.focusArea) {
  // Form data at top level
  formData = firstItem.json;
  debugInfo.dataSource = "Form data found at top level";
}

// Set defaults if no form data found
if (!formData.focusArea || !formData.specificTopic) {
  formData = {
    focusArea: 'General',
    specificTopic: 'RMIS industry trends',
    keyMessage: 'Strategic risk management insights',
    targetAudience: 'Risk Managers and CROs',
    callToAction: 'webinar',
    keyEvents: '',
    additionalNotes: ''
  };
  debugInfo.dataSource += " - using defaults";
}

debugInfo.searchResults = {
  formDataFound: !!formData.focusArea,
  newsDataFound: !!newsData.articles
};

debugInfo.finalData = formData;

// Return merged data
return [{
  json: {
    ...formData,
    newsTitle: 'RMIS Market Growth Continues',
    newsDescription: 'Risk management information systems market shows steady expansion',
    mergedAt: new Date().toISOString(),
    DEBUG_INFO: debugInfo
  }
}];

const items = $input.all();
console.log("=== NEWS FILTER DEBUG ===");
console.log("Items received:", items.length);

// Get the merged data
const mergedData = items[0]?.json;
console.log("FULL mergedData structure:", JSON.stringify(mergedData, null, 2));

// Extract form data correctly
const focusArea = mergedData?.focusArea || 'General';
const specificTopic = mergedData?.specificTopic || 'RMIS trends';

console.log("Extracted focusArea:", focusArea);
console.log("Extracted specificTopic:", specificTopic);
console.log("Extracted keyMessage:", mergedData?.keyMessage);
console.log("Extracted keyEvents:", mergedData?.keyEvents);

// Create relevant articles based on focus area
let articles = [{
  title: `${focusArea} RMIS Market: ${specificTopic} Summit Impact`,
  description: `Risk management information systems in ${focusArea} sector focusing on ${specificTopic} developments and ${mergedData?.keyEvents || 'industry'} context.`,
  publishedAt: "2024-07-24T12:00:00Z",
  url: "https://example.com/rmis-market-growth"
}];

// THE CRITICAL FIX: Create the formData structure exactly as expected
const formDataObject = {
  focusArea: mergedData?.focusArea,
  specificTopic: mergedData?.specificTopic,
  keyMessage: mergedData?.keyMessage,
  targetAudience: mergedData?.targetAudience,
  callToAction: mergedData?.callToAction,
  keyEvents: mergedData?.keyEvents,
  additionalNotes: mergedData?.additionalNotes
};

console.log("Created formData object:", JSON.stringify(formDataObject, null, 2));

// Return enriched article with form data
const enrichedArticle = {
  json: {
    ...articles[0],
    focusArea: focusArea,
    specificTopic: specificTopic,
    keyMessage: mergedData?.keyMessage || 'Strategic insights',
    formData: formDataObject,
    // ALSO ADD DEBUG INFO
    DEBUG_FORMDATA: formDataObject,
    DEBUG_MERGED: mergedData
  }
};

console.log("Final return object:", JSON.stringify(enrichedArticle, null, 2));
return [enrichedArticle];

